/* eslint-disable react/destructuring-assignment */
/**
 * 日视图/列表视图 日历
 */
import React, { FC, TouchEvent, useEffect, useRef, useMemo } from 'react';
import dayjs, { Dayjs } from 'dayjs';
import throttle from 'lodash/throttle';
import cn from 'classnames';
import { animated, useSpring, useSprings } from '@react-spring/web';
import { useDrag, FullGestureState } from '@use-gesture/react';

import {
    formatMonthDate,
    formatWeekDate,
    formatMonthYear,
    mergeProps,
} from '../../utils/index';

import { useSetState } from '../../utils/hooks';

export type CalendarProps = {
    // 选中日期
    currentDate?: Date;
    // 展示方式
    showType?: 'week' | 'month';
    // 动画时间
    transitionDuration?: number;
    // 点击选中日期
    onDateClick?: Function;
    // 拖动开始
    onTouchStart?: Function;
    // 拖动过程
    onTouchMove?: Function;
    // 拖动结束
    onTouchEnd?: (start: number, end: number) => void;
    // 更换视图模式
    onToggleShowType?: Function;
    // 标记日期
    markDates?: Date[];
    // 禁用月视图
    disabledMonthView?: boolean;
};

const defaultFunc = (...rest: any) => {
    // console.log('default function:', rest);
};

const defaultCalendarProps = {
    currentDate: new Date(),
    showType: 'week',
    transitionDuration: 0.3,
    onDateClick: defaultFunc,
    onTouchStart: defaultFunc,
    onTouchMove: defaultFunc,
    onTouchEnd: defaultFunc,
    onToggleShowType: defaultFunc,
    markDates: [],
};

type DragType = Omit<FullGestureState<'drag'>, 'event'> & {
    event: PointerEvent | MouseEvent | globalThis.TouchEvent | KeyboardEvent;
};

type StateValue = {
    currentMonthFirstDay: Dayjs;
    monthDates: Dayjs[][];
    currentWeekFirstDay: Dayjs;
    weekDates: Dayjs[][];
    currentDate: Dayjs;
    touch: { x: number; y: number };
    translateIndex: number;
    calendarY: number;
    showType: CalendarProps['showType'];
    isTouching: boolean;
};

const Calendar: FC<CalendarProps> = p => {
    const props = mergeProps(defaultCalendarProps, p);

    const [state, setState] = useSetState<StateValue>({
        currentMonthFirstDay: dayjs().startOf('month'),
        monthDates: [], // 月日历需要展示的日期 包括前一月 当月 下一月
        currentWeekFirstDay: dayjs().startOf('week'),
        weekDates: [], // 周日历需要展示的日期  包括前一周 当周 下一周
        currentDate: dayjs(),
        touch: { x: 0, y: 0 },
        translateIndex: 0,
        calendarY: 0, // 于Y轴的位置
        showType: props.showType,
        isTouching: false,
    });

    // 是否拖动中
    const isTouchingRef = useRef(false);
    // 日历dom
    const calendarRef = useRef<HTMLDivElement>(null);

    // 收起为周视图, 展开为月视图
    const isMonthView = state.showType === 'month';

    // 更新日期. 模拟 getDerivedStateFromProps
    useEffect(() => {
        const dayjsDate = dayjs(props.currentDate);
        setState({
            ...formatMonthDate(dayjsDate),
            ...formatWeekDate(dayjsDate),
            currentDate: dayjsDate,
        });
    }, [props.currentDate, setState]);

    // 点击选中日期
    const handleDayClick = (dayjsDate: Dayjs) => {
        if (dayjs(state.currentDate).isSame(dayjsDate, 'day')) {
            return;
        }
        setState({
            ...formatMonthDate(dayjsDate),
            ...formatWeekDate(dayjsDate),
            currentDate: dayjsDate,
        });
        props.onDateClick(dayjsDate.toDate());
    };

    // 父容器位移变化
    const [containerStyle, containerApi] = useSpring(() => ({
        from: {
            transform: `translate3d(${-state.translateIndex * 100}%, 0, 0)`,
            transitionDuration: `0s`,
            transitionProperty: 'none'
        },
        config: {
            tension: 100,
            friction: 30,
            clamp: true,
            duration: 300
        },
    }));

    // 父容器常规显示指定选中内容y轴高度
    const pos = useMemo(() => {
        const index = state.monthDates[1]?.findIndex(item =>
            item.isSame(state.currentDate, 'day')
        );
        const pos = Math.floor(index / 7) || 0;
        return -pos * 42;
    }, [state.currentDate, state.monthDates]);

    // 父容器位移数字初始化
    useEffect(() => {
        containerApi.start({
            to: async next => {
                await next({
                    transform: `translate3d(${-state.translateIndex * 100}%, ${
                        pos
                    }px, 0)`,
                    transitionDuration: `${props.transitionDuration}s`,
                    immediate: true,
                });
            },
        });
    }, [
        state.translateIndex,
        containerApi,
        props.transitionDuration,
        pos,
    ]);

    // 子容器横向位移
    const [springStyles, springApi] = useSprings(3, i => ({
        from: {
            transform: `translate3d(${(i - 1 + state.translateIndex) * 100}%, ${
                state.calendarY
            }px, 0)`,
            transitionDuration: `${props.transitionDuration}s`,
        },
        config: {
            tension: 100,
            friction: 30,
            clamp: true,
            duration: 0,
        },
    }));

    // 最外层容器高度
    const [heightStyle, heightApi] = useSpring(() => ({
        from: {
            height: 46,
            transitionDuration: `${props.transitionDuration}s`,
        },
        config: {
            tension: 100,
            friction: 30,
            clamp: true,
        },
    }));

    // useEffect(() => {
    //     heightApi.start({
    //         from: {
    //             height: state.showType === 'week' ? 46 : 260,
    //         },
    //     });
    // }, [state.showType, heightApi]);

    // 横向移动计算
    const runHorizontal = (dragState: DragType) => {
        const [x] = dragState.movement;
        if (dragState.last) {
            isTouchingRef.current = false;
            if (Math.abs(x) > 80) {
                const add = x > 0 ? 1 : -1;
                springApi.start(i => ({
                    to: async next => {
                        await next({
                            transform: `translate3d(${
                                (i - 1 + state.translateIndex + add) * 100
                            }%, ${state.calendarY}px, 0)`,
                            transitionDuration: `${props.transitionDuration}s`,
                        });
                    },
                }));
                setState({
                    translateIndex: state.translateIndex + add,
                    isTouching: false,
                });
                if (isMonthView) {
                    const nextMonthFirstDay = state.currentMonthFirstDay[
                        add > 0 ? 'subtract' : 'add'
                    ](1, 'month');
                    handleDayClick(nextMonthFirstDay);
                } else {
                    const nextWeekFirstDay = state.currentWeekFirstDay[
                        add > 0 ? 'subtract' : 'add'
                    ](1, 'week');
                    handleDayClick(nextWeekFirstDay);
                }
            } else {
                springApi.start(i => ({
                    to: async next => {
                        await next({
                            transform: `translate3d(${
                                (i - 1 + state.translateIndex) * 100
                            }%, ${state.calendarY}px, 0)`,
                            transitionDuration: `${props.transitionDuration}s`,
                        });
                    },
                }));
            }
            return;
        }
        const width = calendarRef.current?.offsetWidth || 1;
        const xPersent = x / width;
        springApi.start(i => ({
            transform: `translate3d(${
                (i - 1 + state.translateIndex + xPersent) * 100
            }%, ${state.calendarY}px, 0)`,
            transitionDuration: `0s`,
            immediate: true,
        }));
    };

    // 纵向移动计算
    const runVertical = (dragState: DragType) => {
        const [, y] = dragState.movement;
        const height = isMonthView ? 260 : 46;
        if (dragState.last) {
            if (Math.abs(y) > 80) {
                const targetHeight = !isMonthView ? 260 : 46;
                if (y > 0) {
                    heightApi.start({
                        to: async next => {
                            await next({
                                height: targetHeight,
                                transitionDuration: '0.3s',
                                immediate: false,
                                config: {
                                    duration: 0.3,
                                },
                            });
                            handleShowTypeToggle();
                        },
                    });
                    containerApi.start({
                        to: async next => {
                            await next({
                                transform: `translate3d(${
                                    -state.translateIndex * 100
                                }%, 0px, 0)`,
                                transitionDuration: `${props.transitionDuration}s`,
                                immediate: true,
                            });
                        },
                    });
                } else {
                    heightApi.start({
                        to: async next => {
                            await next({
                                height: targetHeight,
                                transitionDuration: '0.3s',
                                immediate: false,
                                config: {
                                    duration: 0.3,
                                },
                            });
                            handleShowTypeToggle();
                        },
                    });
                    containerApi.start({
                        to: async next => {
                            await next({
                                transform: `translate3d(${
                                    -state.translateIndex * 100
                                }%, ${pos}px, 0)`,
                                transitionDuration: `${props.transitionDuration}s`,
                                immediate: true,
                            });
                        },
                    });
                }
            } else {
                heightApi.start({
                    to: async next => {
                        await next({
                            height: height,
                            transitionDuration: '0.3s',
                            immediate: false,
                        });
                    },
                });
                containerApi.start({
                    to: async next => {
                        await next({
                            transform: `translate3d(${
                                -state.translateIndex * 100
                            }%, ${isMonthView ? 0 : pos}px, 0)`,
                            transitionDuration: `${props.transitionDuration}s`,
                            immediate: false,
                        });
                    },
                });
            }
            return;
        }
        if (isMonthView) {
            heightApi.start({
                from: {
                    height: Math.max(height + y, 60),
                    transitionDuration: '0s',
                },
            });
            containerApi.start({
                to: async next => {
                    await next({
                        transform: `translate3d(${
                            -state.translateIndex * 100
                        }%, ${Math.max(pos, y * 0.8)}px, 0)`,
                        transitionDuration: `${props.transitionDuration}s`,
                        immediate: true,
                    });
                },
            });
        } else {
            heightApi.start({
                from: {
                    height: Math.min(height + y, 260),
                    transitionDuration: '0s',
                },
            });
            containerApi.start({
                to: async next => {
                    await next({
                        transform: `translate3d(${
                            -state.translateIndex * 100
                        }%, ${Math.min(pos + y * 0.8, 0)}px, 0)`,
                        transitionDuration: `${props.transitionDuration}s`,
                        immediate: true,
                    });
                },
            });
        }
    };

    // 拖动
    useDrag(
        dragState => {
            dragState.event.stopPropagation();
            if (dragState.first) {
                isTouchingRef.current = true;
            }

            if (dragState.axis === 'x') {
                if (!isMonthView && !state.isTouching) {
                    setState({ isTouching: true });
                }
                runHorizontal(dragState);
            }

            if (dragState.axis === 'y') {
                runVertical(dragState);
            }
        },
        {
            pointer: { touch: true },
            target: calendarRef,
            eventOptions: { passive: false },
        }
    );

    // 切换模式
    const handleShowTypeToggle = () => {
        setState({ showType: state.showType === 'month' ? 'week' : 'month' });
    };

    // 可以通过此函数 实现 点击按钮 进行月份切换
    const handleMonthToggle = (type: 'prev' | 'next') => {
        const { currentMonthFirstDay, currentWeekFirstDay, showType } = state;
        const isMonthView = showType === 'month';
        const isPrev = type === 'prev';
        const formatFun = isMonthView ? formatMonthDate : formatWeekDate;
        const operateDate = isMonthView
            ? currentMonthFirstDay
            : currentWeekFirstDay;
        const updateStateData = formatFun(
            operateDate[isPrev ? 'subtract' : 'add'](
                1,
                isMonthView ? 'month' : 'week'
            )
        );
        setState(updateStateData);
        // const cb = () => {
        //     const dataArray = updateStateData[isMonthView ? 'monthDates' : 'weekDates'][1];
        //     onTouchEnd(dataArray[0].valueOf(), dataArray[dataArray.length - 1].add(1, 'day').valueOf());
        // };
        // cb();
    };

    // 格式化日期显示
    const formatDay = (date: Dayjs) => {
        if (dayjs().isSame(date, 'day')) {
            return <span style={{ color: '#3388FF' }}>今</span>;
        }
        // 'YYYY-MM-DD HH:mm:ss'
        return dayjs(date).format('D');
    };

    const head = ['日', '一', '二', '三', '四', '五', '六'];

    // const calendarBodyHeight = (isMonthView ? 252 : 38) + 8;

    console.log(
        '%c 2022-01-26, 339-info: heightStyle',
        'color: fuchsia; background: Aquamarine; font-size: 20px;',
        heightStyle
    ); // Alian-log

    return (
        <div className='react-mob-calendar'>
            {/* 年月 */}
            <div className='calendar-operate'>
                <div>{formatMonthYear(state.currentDate)}</div>
            </div>

            {/* 星期 */}
            <div className='calendar-head'>
                {head.map(i => (
                    <div className='head-cell' key={i}>
                        {i}
                    </div>
                ))}
            </div>

            {/* 日期表格 */}
            <animated.div
                className={cn('calendar-body', { 'week-mode': isMonthView })}
                style={heightStyle}
                ref={calendarRef}>
                <animated.div style={containerStyle}>
                    {springStyles.map((style, i) => {
                        const dateList =
                            !isMonthView && state.isTouching
                                ? state.weekDates
                                : state.monthDates;
                        return (
                            <animated.div
                                className='month-cell'
                                key={i}
                                style={style}>
                                {dateList[i]?.map((date, index) => {
                                    // 选中日期
                                    const isCurrentDay = date.isSame(
                                        state.currentDate,
                                        'day'
                                    );
                                    // 其它月的日期, 在星期的模式下不显示置灰
                                    const isOtherMonthDay =
                                        state.showType === 'week'
                                            ? false
                                            : !date.isSame(
                                                  state.currentMonthFirstDay,
                                                  'month'
                                              );
                                    // 是否为标志过的日期
                                    const isMarkDate = props.markDates.find(d =>
                                        date.isSame(d, 'day')
                                    );
                                    return (
                                        <div
                                            key={index}
                                            className={cn('day-cell', {
                                                'is-other-month-day':
                                                    isOtherMonthDay,
                                                'current-day': isCurrentDay,
                                            })}
                                            onClick={() =>
                                                handleDayClick(date)
                                            }>
                                            <div className='day-text'>
                                                {formatDay(date)}
                                            </div>
                                            {isMarkDate && (
                                                <div className='dot-mark' />
                                            )}
                                        </div>
                                    );
                                })}
                            </animated.div>
                        );
                    })}
                </animated.div>
                <div className='bottom-operate'>
                    {!props.disabledMonthView && (
                        <div
                            role='presentation'
                            onClick={handleShowTypeToggle}
                            className='bottom-operate-btn'
                        />
                    )}
                </div>
            </animated.div>
        </div>
    );
};

export default Calendar;
