/* eslint-disable react/destructuring-assignment */
/**
 * 日视图/列表视图 日历
 */
import React, { FC, useEffect, useRef } from 'react';
import dayjs from 'dayjs';
import { animated, useSpring } from '@react-spring/web';
import { useDrag } from '@use-gesture/react';

import {
    formatMonthData,
    formatWeekData,
    formatMonthYear,
} from '../../utils/index';

import { useSetState } from '../../utils/hooks';

export type CalendarProps = {
    // 选中日期
    currentDate?: Date;
    // 展示方式
    showType?: 'week' | 'month';
    // 动画时间
    transitionDuration?: number;
    // 点击选中日期
    onDateClick?: Function;
    // 拖动开始
    onTouchStart?: Function;
    // 拖动过程
    onTouchMove?: Function;
    // 拖动结束
    onTouchEnd?: (start: number, end: number) => void;
    // 更换视图模式
    onToggleShowType?: Function;
    // 标记日期
    markDates?: Date[];
    // 禁用月视图
    disabledMonthView?: boolean;
}

const Calendar: FC<CalendarProps> = props => {
    const {
        currentDate,
        transitionDuration,
        markDates,
        disabledMonthView,
        onTouchStart,
        onTouchMove,
        onTouchEnd,
        onToggleShowType,
        onDateClick,
    } = props;
    const [state, setState] = useSetState({
        currentMonthFirstDay: null,
        monthDates: [], // 月日历需要展示的日期 包括前一月 当月 下一月
        currentWeekFirstDay: null,
        weekDates: [], // 周日历需要展示的日期  包括前一周 当周 下一周
        currentDate: new Date(),
        touch: { x: 0, y: 0 },
        translateIndex: 0,
        calendarY: 0, // 于Y轴的位置
        showType: props.showType,
    });

    // 是否拖动中
    const isTouchingRef = useRef(false);
    // 拖动起始横坐标
    const touchStartPositionXRef = useRef(0);
    // 拖动起始纵坐标
    const touchStartPositionYRef = useRef(0);
    // 日历dom
    const calendarRef = useRef(null);

    const {
        monthDates,
        weekDates,
        touch,
        translateIndex,
        calendarY,
        currentMonthFirstDay,
        currentWeekFirstDay,
        showType,
    } = state;

    // 更新日期. 模拟 getDerivedStateFromProps
    useEffect(() => {
        if (currentDate !== state.currentDate) {
            const dayjsDate = dayjs(currentDate);
            setState({
                ...formatMonthData(dayjsDate),
                ...formatWeekData(dayjsDate),
                currentDate,
            });
        }
    }, [currentDate]);

    // 点击选中日期
    const handleDayClick = date => {
        if (dayjs(currentDate).isSame(date, 'day')) {
            return;
        }
        onDateClick(date.toDate());
    };

    const handleTouchMove = _.throttle(e => {
        e.stopPropagation();
        const moveX = e.touches[0].clientX - touchStartPositionXRef.current;
        const moveY = e.touches[0].clientY - touchStartPositionYRef.current;
        const calendarWidth = calendarRef.current.offsetWidth;
        const calendarHeight = calendarRef.current.offsetHeight;
        if (Math.abs(moveX) > Math.abs(moveY)) {
            // 左右滑动
            setState({ touch: { x: moveX / calendarWidth, y: 0 } });
        } else if (!disabledMonthView) {
            setState({ touch: { x: 0, y: moveY / calendarHeight } });
        }
        onTouchMove(e);
    }, 25);

    const handleTouchStart = e => {
        e.stopPropagation();
        touchStartPositionXRef.current = e.touches[0].clientX;
        touchStartPositionYRef.current = e.touches[0].clientY;
        isTouchingRef.current = true;
        onTouchStart(e);
    };

    const handleTouchEnd = e => {
        e.stopPropagation();
        const calendarHeight = calendarRef.current.offsetHeight;
        isTouchingRef.current = false;
        const absTouchX = Math.abs(touch.x);
        const absTouchY = Math.abs(touch.y);
        if (absTouchX > absTouchY && absTouchX > 0.15) {
            const isMonthView = showType === 'month';
            // touch.x > 0 右滑 日期减少, 回到上个月, translateIndex变大 向右偏移
            const newTranslateIndex =
                touch.x > 0 ? translateIndex + 1 : translateIndex - 1;

            if (isMonthView) {
                // 月视图
                const nextMonthFirstDay = currentMonthFirstDay[
                    touch.x > 0 ? 'subtract' : 'add'
                ](1, 'month');
                // const nextMonthStartDay = nextMonthFirstDay.startOf('week');
                // const nextMonthEndDay = nextMonthStartDay.add(42, 'day');
                setState({
                    translateIndex: newTranslateIndex,
                    ...formatMonthData(nextMonthFirstDay),
                });
                handleDayClick(nextMonthFirstDay);
                // onTouchEnd(nextMonthStartDay.valueOf(), nextMonthEndDay.valueOf());
            } else {
                // 周视图
                const nexCurrentDay = dayjs(currentDate)[
                    touch.x > 0 ? 'subtract' : 'add'
                ](1, 'week');
                const nextWeekFirstDay = currentWeekFirstDay[
                    touch.x > 0 ? 'subtract' : 'add'
                ](1, 'week');
                // const nextWeekLastDay = nextWeekFirstDay.add(7, 'day');
                setState({
                    translateIndex: newTranslateIndex,
                    ...formatWeekData(nextWeekFirstDay),
                });
                handleDayClick(nexCurrentDay);
                // onTouchEnd(nextWeekFirstDay.valueOf(), nextWeekLastDay.valueOf());
            }
        } else if (
            absTouchY > absTouchX &&
            Math.abs(touch.y * calendarHeight) > 50
        ) {
            if (disabledMonthView) {
                // 禁用周视图
            } else if (touch.y > 0 && showType === 'week') {
                setState({ showType: 'month' });
                // const cb = () => {
                //     const dataArray = state.weekDates[1];
                //     onToggleShowType({
                //         showType: state.showType,
                //         startTime: dataArray[0].valueOf(),
                //         endTime: dataArray[dataArray.length - 1].add(1, 'day').valueOf()
                //     });
                // };
                // cb();
            } else if (touch.y < 0 && showType === 'month') {
                setState({ showType: 'week' });
                // const cb = () => {
                //     const dataArray = state.weekDates[1];
                //     onToggleShowType({
                //         showType: state.showType,
                //         startTime: dataArray[0].valueOf(),
                //         endTime: dataArray[dataArray.length - 1].add(1, 'day').valueOf()
                //     });
                // };
                // cb();
            }
        }
        setState({ touch: { x: 0, y: 0 } });
    };

    const handleShowTypeToggle = () => {
        setState({ showType: showType === 'month' ? 'week' : 'month' });
    };

    // 可以通过此函数 实现 点击按钮 进行月份切换
    const handleMonthToggle = (type: 'prev' | 'next') => {
        const { currentMonthFirstDay, currentWeekFirstDay, showType } = state;
        const isMonthView = showType === 'month';
        const isPrev = type === 'prev';
        const formatFun = isMonthView ? formatMonthData : formatWeekData;
        const operateDate = isMonthView
            ? currentMonthFirstDay
            : currentWeekFirstDay;
        const updateStateData = formatFun(
            operateDate[isPrev ? 'subtract' : 'add'](
                1,
                isMonthView ? 'month' : 'week'
            )
        );
        setState(updateStateData);
        // const cb = () => {
        //     const dataArray = updateStateData[isMonthView ? 'monthDates' : 'weekDates'][1];
        //     onTouchEnd(dataArray[0].valueOf(), dataArray[dataArray.length - 1].add(1, 'day').valueOf());
        // };
        // cb();
    };

    // 收起为周视图, 展开为月视图
    const isMonthView = showType === 'month';

    // 格式化日期显示
    const formatDay = (date: Date) => {
        if (dayjs().isSame(date, 'day')) {
            return <span style={{ color: '#3388FF' }}>今</span>;
        }
        // 'YYYY-MM-DD HH:mm:ss'
        return dayjs(date).format('D');
    };

    const head = ['日', '一', '二', '三', '四', '五', '六'];

    const calendarBodyHeight = (isMonthView ? 252 : 38) + 8;

    return (
        <div className='smart-calendar'>
            {/* 年月 */}
            <div className='calendar-operate'>
                <div>{formatMonthYear(currentDate)}</div>
            </div>

            {/* 星期 */}
            <div className='calendar-head'>
                {head.map(i => (
                    <div className='head-cell' key={i}>
                        {i}
                    </div>
                ))}
            </div>

            {/* 日期表格 */}
            <div
                className={`calendar-body ${isMonthView ? '' : 'week-mode'}`}
                style={{ height: calendarBodyHeight }}
                ref={calendarRef}
                onTouchStart={handleTouchStart}
                onTouchMove={handleTouchMove}
                onTouchEnd={handleTouchEnd}>
                <div
                    style={{
                        transform: `translate3d(${
                            -translateIndex * 100
                        }%, 0, 0)`,
                    }}>
                    {(isMonthView ? monthDates : weekDates).map(
                        (item, index) => {
                            return (
                                <div
                                    className='month-cell'
                                    key={`month-cell-${index}`}
                                    style={{
                                        transform: `translate3d(${
                                            (index -
                                                1 +
                                                translateIndex +
                                                (isTouchingRef.current
                                                    ? touch.x
                                                    : 0)) *
                                            100
                                        }%, ${calendarY}px, 0)`,
                                        transitionDuration: `${
                                            isTouchingRef.current
                                                ? 0
                                                : transitionDuration
                                        }s`,
                                    }}>
                                    {item.map((date, itemIndex) => {
                                        // 选中日期
                                        const isCurrentDay = date.isSame(
                                            currentDate,
                                            'day'
                                        );
                                        // 其它月的日期, 在星期的模式下不显示置灰
                                        const isOtherMonthDay =
                                            showType === 'week'
                                                ? false
                                                : !date.isSame(
                                                      currentMonthFirstDay,
                                                      'month'
                                                  );
                                        // 是否为标志过的日期
                                        const isMarkDate = markDates.find(d =>
                                            date.isSame(d, 'day')
                                        );
                                        return (
                                            <div
                                                key={itemIndex}
                                                className={`day-cell ${
                                                    isOtherMonthDay
                                                        ? 'is-other-month-day'
                                                        : ''
                                                } ${
                                                    isCurrentDay
                                                        ? 'current-day'
                                                        : ''
                                                }`}
                                                onClick={() =>
                                                    handleDayClick(date)
                                                }>
                                                <div className='day-text'>
                                                    {formatDay(date)}
                                                </div>
                                                {isMarkDate && (
                                                    <div className='dot-mark' />
                                                )}
                                            </div>
                                        );
                                    })}
                                </div>
                            );
                        }
                    )}
                </div>
                <div className='bottom-operate'>
                    {!disabledMonthView && (
                        <div
                            role='presentation'
                            onClick={handleShowTypeToggle}
                            className='bottom-operate-btn'
                        />
                    )}
                </div>
            </div>
        </div>
    );
};

Calendar.defaultProps = {
    currentDate: new Date(),
    showType: 'week',
    transitionDuration: 0.3,
    onDateClick: () => {},
    onTouchStart: () => {},
    onTouchMove: () => {},
    onTouchEnd: () => {},
    onToggleShowType: () => {},
    markDates: [],
    disabledMonthView: false,
};

export default Calendar;
